hudson.tasks.Shell.command:
pipeline {
    agent any
    stages {
        stage('Trigger Jobs') {
            steps {
                sh '''
                  JENKINS_URL="http://localhost:8080"
                  USER="admin"
                  API_TOKEN="119945a0409c8335bfdb889b602739a995"

                  curl -s -X POST "$JENKINS_URL/job/job1/build" --user "$USER:$API_TOKEN"
                  curl -s -X POST "$JENKINS_URL/job/job2/buildWithParameters?BRANCH=main" --user "$USER:$API_TOKEN"
                '''
            }
        }
    }
}
